["colorFunc"] = "function(progress, r1, g1, b1, a1, r2, g2, b2, a2)\n    local chp = UnitHealth(\"party1\")\n    local mhp = UnitHealthMax(\"party1\")\n    \n    if chp <= (mhp * 0.12) then\n        r, g, b = 1,0,0\n    elseif chp > (mhp * 0.12) and chp <= (mhp * 0.16) then\n        r, g, b = 1,0.1,0\n    elseif chp > (mhp * 0.16) and chp <= (mhp * 0.20) then\n        r, g, b = 1,0.2,0\n    elseif chp > (mhp * 0.20) and chp <= (mhp * 0.24) then\n        r, g, b = 1,0.3,0     \n    elseif chp > (mhp * 0.24) and chp <= (mhp * 0.28) then\n        r, g, b = 1,0.4,0\n    elseif chp > (mhp * 0.28) and chp <= (mhp * 0.32) then\n        r, g, b = 1,0.5,0\n    elseif chp > (mhp * 0.32) and chp <= (mhp * 0.36) then\n        r, g, b = 1,0.6,0\n    elseif chp > (mhp * 0.36) and chp <= (mhp * 0.4) then\n        r, g, b = 1,0.7,0     \n    elseif chp > (mhp * 0.4) and chp <= (mhp * 0.44) then\n        r, g, b = 1,0.8,0\n    elseif chp > (mhp * 0.44) and chp <= (mhp * 0.48) then\n        r, g, b = 1,0.9,0\n    elseif chp > (mhp * 0.48) and chp <= (mhp * 0.52) then\n        r, g, b = 1,1,0\n    elseif chp > (mhp * 0.52) and chp <= (mhp * 0.56) then\n        r, g, b = 0.9,1,0   \n    elseif chp > (mhp * 0.56) and chp <= (mhp * 0.6) then\n        r, g, b = 0.8,1,0\n    elseif chp > (mhp * 0.6) and chp <= (mhp * 0.64) then\n        r, g, b = 0.7,1,0\n    elseif chp > (mhp * 0.64) and chp <= (mhp * 0.68) then\n        r, g, b = 0.6,1,0\n    elseif chp > (mhp * 0.68) and chp <= (mhp * 0.72) then\n        r, g, b = 0.5,1,0     \n    elseif chp > (mhp * 0.72) and chp <= (mhp * 0.76) then\n        r, g, b = 0.4,1,0\n    elseif chp > (mhp * 0.76) and chp <= (mhp * 0.80) then\n        r, g, b = 0.3,1,0\n    elseif chp > (mhp * 0.80) and chp <= (mhp * 0.84) then\n        r, g, b = 0.2,1,0\n    elseif chp > (mhp * 0.84) and chp <= (mhp * 0.88) then\n        r, g, b = 0.1,1,0\n    elseif chp > (mhp * 0.88) and chp <= mhp then\n        r, g, b = 0,1,0        \n    end\n    \n    for i=1,40 do\n        if aura_env.dispelTypes[select(5,UnitDebuff('party1',i))] then\n            return DebuffTypeColor\n        end\n    end\n    \n    if UnitIsEnemy(\"party1\", \"player\") then\n        r, g, b = 1,0,0\n        --if UnitIsEnemy(\"target\", \"player\") then\n        --       r, g, b = 1,0,0\n        \n        --    elseif UnitReaction(\"target\", \"player\") <4 then\n        --        r, g, b = 1,0,0\n    elseif UnitReaction(\"party1\", \"player\") ==4 then\n        r, g, b = 1,1,0\n    end\n    \n    \n    if UnitIsAFK(\"party1\") then\n        r, g, b = 0.7,0.7,0.7\n    end\n    \n    \n    local alpha = 1\n    local min,max = aura_env.GetRange('party1')\n    if min and min > 16 then\n        alpha = .4\n    end\n    return r, g, b, alpha\n    \n    \n    \nend\n\n\n",
